mapper.select:
SELECT 
FROM ums_third_party_auth
WHERE provider = #{provider,jdbcType=TINYINT}
  AND third_party_id = #{thirdPartyId,jdbcType=VARCHAR}
  AND status = 1

SELECT 
FROM ums_third_party_auth
WHERE member_id = #{memberId,jdbcType=BIGINT}
  AND provider = #{provider,jdbcType=TINYINT}
  AND status = 1

mapper.delete:
DELETE FROM ums_third_party_auth 
WHERE member_id = #{memberId,jdbcType=BIGINT} 
  AND provider = #{provider,jdbcType=TINYINT}
